create table "public"."collection_has_duas" (
    "created_at" timestamp with time zone not null default now(),
    "updated_at" timestamp with time zone,
    "dua_id" bigint not null,
    "collection_id" bigint not null,
    "position" bigint
);


alter table "public"."collection_has_duas" enable row level security;

create table "public"."collection_translations" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "updated_at" timestamp with time zone,
    "title" text,
    "description" text,
    "seo_description" text,
    "language_code" text,
    "collection_id" bigint not null
);


alter table "public"."collection_translations" enable row level security;

create table "public"."collections" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "updated_at" timestamp with time zone,
    "slug" text not null,
    "image_url" text,
    "page_views" bigint not null default '0'::bigint,
    "api_calls" bigint not null default '0'::bigint,
    "type" text
);


alter table "public"."collections" enable row level security;

create table "public"."post_translations" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "updated_at" timestamp with time zone,
    "post_id" bigint not null,
    "language_code" text not null,
    "slug" text not null,
    "excerpt" text not null,
    "body_markdown" text not null
);


alter table "public"."post_translations" enable row level security;

create table "public"."posts" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "updated_at" timestamp with time zone,
    "cover_image_url" text
);


alter table "public"."posts" enable row level security;

CREATE UNIQUE INDEX collection_has_duas_pkey ON public.collection_has_duas USING btree (dua_id, collection_id);

CREATE UNIQUE INDEX collection_translations_pkey ON public.collection_translations USING btree (id);

CREATE UNIQUE INDEX collections_pkey ON public.collections USING btree (id);

CREATE UNIQUE INDEX collections_slug_key ON public.collections USING btree (slug);

CREATE UNIQUE INDEX post_translations_pkey ON public.post_translations USING btree (id);

CREATE UNIQUE INDEX post_translations_slug_key ON public.post_translations USING btree (slug);

CREATE UNIQUE INDEX posts_pkey ON public.posts USING btree (id);

alter table "public"."collection_has_duas" add constraint "collection_has_duas_pkey" PRIMARY KEY using index "collection_has_duas_pkey";

alter table "public"."collection_translations" add constraint "collection_translations_pkey" PRIMARY KEY using index "collection_translations_pkey";

alter table "public"."collections" add constraint "collections_pkey" PRIMARY KEY using index "collections_pkey";

alter table "public"."post_translations" add constraint "post_translations_pkey" PRIMARY KEY using index "post_translations_pkey";

alter table "public"."posts" add constraint "posts_pkey" PRIMARY KEY using index "posts_pkey";

alter table "public"."collection_has_duas" add constraint "collection_has_duas_collection_id_fkey" FOREIGN KEY (collection_id) REFERENCES collections(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;

alter table "public"."collection_has_duas" validate constraint "collection_has_duas_collection_id_fkey";

alter table "public"."collection_has_duas" add constraint "collection_has_duas_dua_id_fkey" FOREIGN KEY (dua_id) REFERENCES duas(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;

alter table "public"."collection_has_duas" validate constraint "collection_has_duas_dua_id_fkey";

alter table "public"."collection_translations" add constraint "collection_translations_collection_id_fkey" FOREIGN KEY (collection_id) REFERENCES collections(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;

