alter table "public"."collection_translations" add constraint "collection_translations_language_code_fkey" FOREIGN KEY (language_code) REFERENCES languages(code) ON UPDATE CASCADE not valid;

alter table "public"."collection_translations" validate constraint "collection_translations_language_code_fkey";

alter table "public"."collections" add constraint "collections_slug_key" UNIQUE using index "collections_slug_key";

alter table "public"."post_translations" add constraint "post_translations_language_code_fkey" FOREIGN KEY (language_code) REFERENCES languages(code) ON UPDATE CASCADE ON DELETE CASCADE not valid;

alter table "public"."post_translations" validate constraint "post_translations_language_code_fkey";

alter table "public"."post_translations" add constraint "post_translations_post_id_fkey" FOREIGN KEY (post_id) REFERENCES posts(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;

alter table "public"."post_translations" validate constraint "post_translations_post_id_fkey";

alter table "public"."post_translations" add constraint "post_translations_slug_key" UNIQUE using index "post_translations_slug_key";

